\documentclass[border=10pt]{standalone}
\usepackage{tikz}
\usetikzlibrary{shapes,arrows,positioning,backgrounds}

\begin{document}

\tikzstyle{process} = [rectangle, rounded corners, minimum width=3cm, minimum height=1cm, text centered, draw=orange, fill=orange!30]
\tikzstyle{material} = [rectangle, minimum width=3cm, minimum height=1cm, text centered, draw=green, fill=green!30]
\tikzstyle{emission} = [ellipse, minimum width=3cm, minimum height=1cm, text centered, draw=red, fill=red!30]
\tikzstyle{metric} = [diamond, minimum width=3cm, minimum height=1cm, text centered, draw=blue, fill=blue!30]
\tikzstyle{prevention} = [trapezium, trapezium left angle=70, trapezium right angle=110, minimum width=3cm, minimum height=1cm, text centered, draw=purple, fill=purple!30]
\tikzstyle{profile} = [regular polygon,regular polygon sides=6, minimum width=3cm, minimum height=1cm, text centered, draw=brown, fill=brown!30]
\tikzstyle{arrow} = [thick,->,>=stealth]

\begin{tikzpicture}[node distance=2cm]

\node (sawmill) [process] {Sawmill Operations};
\node (raw) [process, below left=of sawmill] {Raw Material Processing};
\node (sawing) [process, below=of sawmill] {Sawing};
\node (planing) [process, below right=of sawmill] {Planing};
\node (waste) [material, below=of sawing] {Wood Waste};
\node (emissions) [emission, below=of waste] {Particulate Emissions};
\node (fine) [emission, below left=of emissions] {Fine Particulates (PM2.5)};
\node (coarse) [emission, below right=of emissions] {Coarse Particulates (PM10)};
\node (aqi) [metric, left=of emissions] {Air Quality Index};
\node (control) [prevention, right=of emissions] {Dust Control Measures};
\node (profile) [profile, below=of emissions] {Daily Emission Profile};

\draw [arrow] (sawmill) -- node[anchor=east] {includes} (raw);
\draw [arrow] (sawmill) -- node[anchor=west] {includes} (sawing);
\draw [arrow] (sawmill) -- node[anchor=west] {includes} (planing);
\draw [arrow] (raw) -- node[anchor=east] {produces} (waste);
\draw [arrow] (sawing) -- node[anchor=west] {produces} (waste);
\draw [arrow] (planing) -- node[anchor=west] {produces} (waste);
\draw [arrow] (waste) -- node[anchor=west] {generates} (emissions);
\draw [arrow] (emissions) -- node[anchor=east] {contains} (fine);
\draw [arrow] (emissions) -- node[anchor=west] {contains} (coarse);
\draw [arrow] (emissions) -- node[anchor=south] {affects} (aqi);
\draw [arrow] (control) -- node[anchor=south] {reduces} (emissions);
\draw [arrow] (emissions) -- node[anchor=west] {tracked by} (profile);

\begin{pgfonlayer}{background}
\path (sawmill.west |- sawmill.north)+(-0.5,0.5) node (a) {};
\path (profile.south -| profile.east)+(0.5,-0.5) node (b) {};
\path[fill=yellow!20,rounded corners, draw=black!50, dashed]
    (a) rectangle (b);
\end{pgfonlayer}

\end{tikzpicture}

\end{document}

