\documentclass[border=10pt]{standalone}
\usepackage{tikz}
\usetikzlibrary{shapes.geometric, arrows, positioning, calc}

\begin{document}

\begin{tikzpicture}[
    node distance = 2cm and 4cm,
    box/.style = {
        rectangle,
        rounded corners,
        draw=red!80,
        fill=red!10,
        text width=5cm,
        minimum height=2cm,
        align=center,
        font=\sffamily\small
    },
    arrow/.style = {
        ->,
        thick,
        red!50,
        >=stealth
    }
]

% Nodes
\node[box] (onboarding) {Onboarding\\\small\textit{Introduction to Red Cross Mission and Values}};
\node[box, below=of onboarding] (basicFirstAid) {Basic First Aid\\\small\textit{Basic lifesaving techniques and CPR}};
\node[box, below=of basicFirstAid] (volunteerCodeOfConduct) {Volunteer Code of Conduct\\\small\textit{Understanding roles, responsibilities, and ethical guidelines}};
\node[box, below=of volunteerCodeOfConduct] (emergencyResponseTraining) {Emergency Response Training\\\small\textit{Disaster response and safety procedures}};
\node[box, below=of emergencyResponseTraining] (psychologicalFirstAid) {Psychological First Aid\\\small\textit{Providing emotional support in crises}};

\node[box, right=of $(onboarding.east)!0.5!(basicFirstAid.east)$] (specializedTraining) {Specialized Training\\\small\textit{Specific training based on volunteer's role}};
\node[box, below=of specializedTraining] (continuingEducation) {Continuing Education\\\small\textit{Ongoing learning and skill enhancement}};
\node[box, below=of continuingEducation] (advancedCertifications) {Advanced Certifications\\\small\textit{Specialized certifications}};
\node[box, below=of advancedCertifications] (leadershipTraining) {Leadership Training\\\small\textit{Cultivating leadership and team management skills}};
\node[box, below=of leadershipTraining] (communityEngagement) {Community Engagement\\\small\textit{Building relationships and outreach activities}};

% Arrows
\draw[arrow] (onboarding) -- (basicFirstAid);
\draw[arrow] (basicFirstAid) -- (volunteerCodeOfConduct);
\draw[arrow] (volunteerCodeOfConduct) -- (emergencyResponseTraining);
\draw[arrow] (emergencyResponseTraining) -- (psychologicalFirstAid);
\draw[arrow] (psychologicalFirstAid) -- (specializedTraining);
\draw[arrow] (specializedTraining) -- (continuingEducation);
\draw[arrow] (continuingEducation) -- (advancedCertifications);
\draw[arrow] (advancedCertifications) -- (leadershipTraining);
\draw[arrow] (leadershipTraining) -- (communityEngagement);

% Title
\node[font=\Large\bfseries\sffamily, text=red!80, above=1cm of onboarding] {Red Cross Volunteer Training Pathway};

\end{tikzpicture}

\end{document}

